(()=>{const t=document.getElementById("submit"),e=document.getElementById("country"),n=document.getElementById("latitude"),o=document.getElementById("longitude"),c=document.getElementById("name"),m=document.getElementById("time"),d=document.getElementById("cloud"),i=document.getElementById("condition"),u=document.getElementById("humidity"),r=document.getElementById("precipitation"),a=document.getElementById("temperature"),l=document.getElementById("wind");t.addEventListener("click",(function(t){t.preventDefault(),async function(t,p){const y=`https://api.weatherapi.com/v1/current.json?key=4968ce87e17e44e083313750231310&q=${document.getElementById("prompt").value}&aqi=no`;await fetch(y,{mode:"cors"}).then((function(t){return t.json()})).then((function(t){e.textContent=t.location.country,n.textContent=t.location.lat,o.textContent=t.location.lon,c.textContent=t.location.name,m.textContent=t.location.localtime,d.textContent=t.current.cloud,i.textContent=t.current.condition.text,u.textContent=t.current.humidity,r.textContent=t.current.precip_mm,a.textContent=t.current.temp_c,l.textContent=t.current.wind_mph,console.log(t)}))}()}));var p=L.map("map").setView([51.505,-.09],13);L.tileLayer("https://tile.openstreetmap.org/{z}/{x}/{y}.png",{maxZoom:19,attribution:'&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>'}).addTo(p)})();