(()=>{const t=document.getElementById("submit"),e=document.getElementById("country"),n=document.getElementById("latitude"),o=document.getElementById("longitude"),c=document.getElementById("name"),m=document.getElementById("time"),d=document.getElementById("cloud"),u=document.getElementById("condition"),i=document.getElementById("humidity"),l=document.getElementById("precipitation"),r=document.getElementById("temperature"),a=document.getElementById("wind");t.addEventListener("click",(function(t){t.preventDefault(),async function(t,y){const g=`https://api.weatherapi.com/v1/current.json?key=4968ce87e17e44e083313750231310&q=${document.getElementById("prompt").value}&aqi=no`;await fetch(g,{mode:"cors"}).then((function(t){return t.json()})).then((function(t){e.textContent=t.location.country,n.textContent=t.location.lat,o.textContent=t.location.lon,c.textContent=t.location.name,m.textContent=t.location.localtime,d.textContent=t.current.cloud,u.textContent=t.current.condition.text,i.textContent=t.current.humidity,l.textContent=t.current.precip_mm,r.textContent=t.current.temp_c,a.textContent=t.current.wind_mph,console.log(t)}))}()}))})();